@AbapCatalog.viewEnhancementCategory: [#NONE]
@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'Value Help Pedido Item'
@Metadata.ignorePropagatedAnnotations: true
define view entity /EXEDMINB/I_PedidoItem_VH
  as select from I_PurchaseOrderAPI01     as Pedido
    inner join   I_PurchaseOrderItemAPI01 as Item on Pedido.PurchaseOrder = Item.PurchaseOrder

  association [0..1] to I_Supplier              as Fornecedor on  $projection.Fornecedor = Fornecedor.Supplier

  association [0..*] to /EXEDMINB/I_PED_ABERTOS as PedAbertos on  $projection.Pedido = PedAbertos.Pedido
                                                              and $projection.Item   = PedAbertos.ItemPedido
{
  key Item.PurchaseOrder             as Pedido,
  key Item.PurchaseOrderItem         as Item,
      Pedido.Supplier                as Fornecedor,
      Fornecedor.TaxNumber1          as CNPJ,
      Fornecedor.SupplierName        as NomeFornecedor,
      Item.PurchaseOrderItemText     as Descricao,
      Item.PurchaseOrderQuantityUnit as Unidade,
      @Semantics.quantity.unitOfMeasure: 'Unidade'
      Item.OrderQuantity             as Quantidade,
      @Semantics.quantity.unitOfMeasure: 'Unidade'
      PedAbertos.QtdEmAberto,
      concat($projection.Pedido, concat('/', $projection.Item)) as PedidoItem
}
