@ClientHandling.type: #CLIENT_DEPENDENT
@AbapCatalog.deliveryClass: #APPLICATION_DATA
@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'NFe Tax ICMS 15'
define table entity /EXEDMINB/T_NFeTaxICMS15
{
  key Id             : abap.char(44);  // FK para Header (chNFe)
  key ItemId         : abap.int4;      // FK para item (NFeItem)

      orig           : abap.char(1);   // Origem da mercadoria
      CST            : abap.char(2);   // Código da Situação Tributária (15)
      qBCMono        : abap.dec(15,4); // Quantidade tributada
      adRemICMS      : abap.dec(15,4); // Alíquota ad rem (valor por unidade)
      vICMSMono      : abap.dec(15,2); // Valor do ICMS monofásico
      qBCMonoReten   : abap.dec(15,4); // Quantidade tributada para retenção
      adRemICMSReten : abap.dec(15,4); // Alíquota ad rem da retenção
      vICMSMonoReten : abap.dec(15,2); // Valor do ICMS monofásico retido
}
